string = DATA.read
require 'set'
new_strings = Set.new
replacements.each_with_index do |(pattern, new_substring), i|
  replaced = ''
  remaining = string
  string.length.times do |n|
    printf("Rule: %3i  Replacements: %3i  Unique Results: %10i\r", i, n, new_strings.length)
   
    new_string = replaced + remaining.sub(pattern) do
      replaced = replaced + $` + $&
      remaining = $'
      new_substring
    end

    if new_string != string
      new_strings.add new_string
    else
      break
    end
  end
end
puts "#{new_strings.length}"

